import React from "react";
import "../styles/about-me.css";
import PopInSection from "./PopInSection"; 

class AboutMe extends React.Component {
    constructor() {
    super();
    this.state = {
        expanded: true,
        activeKey: "1"
    };
    this.handleSelect = this.handleSelect.bind(this); 
    }
    handleSelect(eventKey) {
        this.setState({
            activeKey: eventKey
        })
    }

    render () {

        const myTechStack = [
            "Javascript ES6+",
            "Python",
            "React.JS", 
            "Racket",
            "Node.JS",
            "HTML & CSS"
        ];

        const technologies = myTechStack.map(stack => <li>{stack}</li>); 

        const moreDetails = (
            <p>I am currently a second-year at
                <a href="https://www.northwestern.edu/about/" target={"_blank"}>{" "}
                 Northwestern University
                </a>
                {" "}
                where I am pursuing my Bachelors of Arts and Masters of Science in 
                <b> Computer Science</b>{". "}
                After finishing my studies, I hope to work full-time as a <b> Software Engineer </b>
                and in the future, earn my Ph.D in Computer Science, further advancing the field of <b> Human-Computer Interaction</b>
            </p>
        );

        const NorthwesternWork = (
            <p>
                At Northwestern, I am affliated with the <b><a href="https://tiilt.northwestern.edu/">{" "}Technological Innovations for Inclusive Learning and Teaching (tiilt) </a></b> 
                research group where I work on implementing front-end features 
                for the multimodal-learning analytics platform: <a href="https://tiilt.northwestern.edu/projects/blinc/index.html">{" "}B.L.I.N.C</a> 
                and occansionally on the <a href="https://tiilt.northwestern.edu/projects/sportsanalytics/index.html">{" "}SportsSense</a> project. 

                I also am an <b>undergradaute teaching assistant</b> for the following: 
                <ul>
                    <li>COMP_SCI 214: Data Structures and Algorithms</li>
                    Summer 2022, Fall 2022, Winter 2023
                    <br></br>
                    Teaches the design, implementation, analysis, and proper application of abstract data types,
                    data structures, and their associated algorithms. We will explore a wide variety of data structures both
                    conceptually and concretely via implementation.
                    <br></br>
                    Taught by <a href="https://www.mccormick.northwestern.edu/research-faculty/directory/profiles/st-amour-vincent.html">Vincent St-Amour</a>
                </ul>

                In the Fall 2022 Quarter, I was fortnatue to have been awarded the Northwestern Computer Science department <b>Outstanding Peer Mentor Award</b>{" "}
                in recoginotion of the dedication and quality I exhibited in my teaching and support towards students. 


            </p>
        );

        // const OutSideWork = (
        //     <p>
        //         Outside of my academics, you can see me working with some amazing student organizations:
        //         <ul>
        //             <li>Society of Hispanic Professional Engineers (SHPE)</li>
        //             MentorSHPE Chair
        //             <br></br>
        //             As the MentorSHPE Chair for SHPE, I am responsible for cordinating and supervising our mentorship
        //             program that enables first-generation low-income STEM/Engineerning Majors at Northwestern to be recive 
        //             mentorship and guidance on navigating academics, professional goals, and overall personal development.
        //             <li>Emerging Coders</li>
        //             WebMaster
        //             <br></br>
        //             Seeing more folks that come from the same background in me in technology is important to me. At Emerging Coders,
        //             we aim to accomplish that through networking events, panels, and informational sessions. My role in particular, 
        //             enables me to lead bi-weekly LeetCode review sessions where I prepare and inform our members on preparing for
        //             technical interviews
        //             <li>Alianza</li>
        //             Historian
        //             <br></br>
        //             At Alianza, we are tasked with uniting the Latine community and its allies at Northwestern through community 
        //             and social events. As the historian, I am tasked with maintaing a solid archive of our impact for future generations
        //             of Wildcats to  view. 
        //         </ul>

        //         When I'm not working, you'll catch me exploring my hometown, Chicago, trying out new resturants, playing video games, 
        //         listening to Omar Apollo, and touring new muesuems and art exhibits. 
        //     </p>
        // );

        const all_desc_items = [moreDetails, NorthwesternWork];   

        return (
            <div id="about">
              <PopInSection>
                <div className="section-header ">
                  <span className="section-title">{"<about> me </about>"}</span>
                </div>
                <div className="about-content">
                  <div className="about-description">
                    {[moreDetails]}
                    {"Here are some technologies I have been working with:"}
                    <ul className="tech-stack">
                      {technologies.map(function (tech_item, i) {
                        return (
                          <PopInSection delay={`${i + 1}00ms`}>
                            {tech_item}
                          </PopInSection>
                        );
                      })}
                    </ul>
                    {[no]}
                  </div>
                  <div className="about-image">
                    <img src={"/assets/yo.jpg"} />
                  </div>
                </div>
              </PopInSection>
            </div>
          );
    }

}

export default AboutMe;
